const DiscoveryStory = () => {
    const { lang } = useLanguage();
    const { id } = useParams();
    const navigate = useNavigate();
    const story = STORIES_DATA[id as keyof typeof STORIES_DATA];

    const [currentSlide, setCurrentSlide] = useState(0);
    const [isPlaying, setIsPlaying] = useState(true);
    const [isMuted, setIsMuted] = useState(false);
    const [progress, setProgress] = useState(0);
    const [showTrivia, setShowTrivia] = useState(false);
    const [audioError, setAudioError] = useState(false);
    const { playSound } = useSoundFX();
    const { isMuted: globalMuted, setIsMuted: setGlobalMuted } = useAudio();

    // Mouse Parallax Values
    const mouseX = useMotionValue(0);
    const mouseY = useMotionValue(0);

    const springConfig = { damping: 20, stiffness: 100 };
    const springX = useSpring(mouseX, springConfig);
    const springY = useSpring(mouseY, springConfig);

    const bgX = useTransform(springX, [-0.5, 0.5], ["-3%", "3%"]);
    const bgY = useTransform(springY, [-0.5, 0.5], ["-3%", "3%"]);

    const audioRef = useRef<HTMLAudioElement | null>(null);
    const timerRef = useRef<any>(null);

    // Gyroscope & Mouse Parallax Support
    useEffect(() => {
        const handleDeviceOrientation = (e: DeviceOrientationEvent) => {
            if (e.beta !== null && e.gamma !== null) {
                // Normalize tilt (range roughly -30 to 30 degrees)
                const x = (e.gamma / 30);
                const y = ((e.beta - 45) / 30); // Offset 45 for natural holding angle
                mouseX.set(Math.max(-1, Math.min(1, x)));
                mouseY.set(Math.max(-1, Math.min(1, y)));
            }
        };

        const handleMouseMove = (e: MouseEvent) => {
            const x = (e.clientX / window.innerWidth) - 0.5;
            const y = (e.clientY / window.innerHeight) - 0.5;
            mouseX.set(x * 2); // Double mouse intensity for responsiveness
            mouseY.set(y * 2);
        };

        window.addEventListener("deviceorientation", handleDeviceOrientation);
        window.addEventListener("mousemove", handleMouseMove);

        return () => {
            window.removeEventListener("deviceorientation", handleDeviceOrientation);
            window.removeEventListener("mousemove", handleMouseMove);
        };
    }, []);

    useEffect(() => {
        if (!story) {
            navigate("/404");
            return;
        }

        const audio = new Audio(story.ambient);
        audio.loop = true;
        audio.volume = 0.7;
        audioRef.current = audio;

        const attemptPlay = () => {
            audio.play().catch(() => {
                setAudioError(true);
            });
        };

        attemptPlay();

        const unlockAudio = () => {
            if (audioRef.current) {
                audioRef.current.play()
                    .then(() => setAudioError(false))
                    .catch(() => { });
                window.removeEventListener("click", unlockAudio);
            }
        };
        window.addEventListener("click", unlockAudio);

        return () => {
            audio.pause();
            audioRef.current = null;
            window.removeEventListener("click", unlockAudio);
        };
    }, [story, id, navigate]);

    useEffect(() => {
        if (!isPlaying || showTrivia) {
            if (timerRef.current) clearInterval(timerRef.current);
            return;
        }

        const interval = 50;
        const stepSize = (interval / SLIDE_DURATION) * 100;

        timerRef.current = setInterval(() => {
            setProgress((prev) => {
                if (prev >= 100) {
                    nextSlide();
                    return 0;
                }
                return prev + stepSize;
            });
        }, interval);

        return () => {
            if (timerRef.current) clearInterval(timerRef.current);
        };
    }, [isPlaying, currentSlide, showTrivia]);

    const toggleAudio = (e: React.MouseEvent) => {
        e.stopPropagation();
        playSound("click");
        if (!audioRef.current) return;

        if (isMuted) {
            audioRef.current.play().catch(() => { });
            setIsMuted(false);
            setGlobalMuted(false);
        } else {
            audioRef.current.pause();
            setIsMuted(true);
            setGlobalMuted(true);
        }
    };

    const nextSlide = () => {
        playSound("whoosh");
        setProgress(0);
        setShowTrivia(false);
        if (currentSlide < story.slides.length - 1) {
            setCurrentSlide(currentSlide + 1);
        } else {
            setCurrentSlide(0);
        }
    };

    const prevSlide = () => {
        playSound("whoosh");
        setProgress(0);
        setShowTrivia(false);
        if (currentSlide > 0) {
            setCurrentSlide(currentSlide - 1);
        } else {
            navigate(-1);
        }
    };

    if (!story) return null;
    const slide = story.slides[currentSlide];

    return (
        <div
            className="fixed inset-0 bg-black text-white overflow-hidden flex flex-col z-[9999] font-sans"
            onMouseDown={() => setIsPlaying(false)}
            onMouseUp={() => setIsPlaying(true)}
            onTouchStart={() => setIsPlaying(false)}
            onTouchEnd={() => setIsPlaying(true)}
        >
            <SEO
                title={`${story.title} | Discovery Story`}
                description={lang === "ID"
                    ? `Jelajahi kisah tentang ${story.title} di ${story.location}. Temukan fragmen sejarah, misteri, dan ritual unik.`
                    : `Explore the story of ${story.title} in ${story.location}. Discover fragments of history, mystery, and unique rituals.`}
            />
            <Letterbox />

            {/* Audio Unlock Floating Hint */}
            {audioError && !isMuted && (
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="absolute bottom-32 left-1/2 -translate-x-1/2 z-[10005] bg-[#C5A065] text-black px-6 py-3 rounded-full font-bold text-[10px] uppercase tracking-widest shadow-2xl flex items-center gap-3"
                    onClick={() => {
                        if (audioRef.current) audioRef.current.play();
                        setAudioError(false);
                    }}
                >
                    <Volume2 size={16} className="animate-pulse" />
                    Ketuk Layar Untuk Mengaktifkan Suara
                </motion.div>
            )}

            {/* Header / Navigation Overlay */}
            <header className="fixed top-0 left-0 right-0 h-auto px-4 md:px-12 z-[20000] flex items-center justify-between pointer-events-none py-6 safe-top">
                <div className="flex-1 hidden md:block" /> {/* Spacer */}

                {/* Progress Indicators - Top center on mobile, wider on desktop */}
                <div className="flex-[2] md:flex-1 max-w-sm flex gap-1 md:gap-2 pointer-events-auto items-center">
                    {story.slides.map((_, idx) => (
                        <div key={idx} className="h-[2px] flex-1 bg-white/10 rounded-full overflow-hidden">
                            <motion.div
                                className="h-full bg-[#C5A065]"
                                initial={{ width: "0%" }}
                                animate={{
                                    width: idx < currentSlide ? "100%" : idx === currentSlide ? `${progress}%` : "0%"
                                }}
                                transition={{ duration: 0.1 }}
                            />
                        </div>
                    ))}
                </div>

                <div className="flex-1 flex items-center justify-end gap-4 md:gap-8 pointer-events-auto">
                    <button
                        onClick={toggleAudio}
                        className={`w-9 h-9 md:w-10 md:h-10 rounded-full border flex items-center justify-center transition-all ${isMuted ? 'text-white/20 border-white/5' : 'text-[#C5A065] border-[#C5A065]/30 bg-[#C5A065]/5'}`}
                    >
                        {isMuted ? <VolumeX size={16} /> : <Volume2 size={16} className={audioError ? 'animate-pulse' : ''} />}
                    </button>

                    <button
                        onClick={() => navigate("/")}
                        className="w-9 h-9 md:w-10 md:h-10 rounded-full border border-white/10 flex items-center justify-center text-white/50 hover:text-white hover:border-[#C5A065] hover:bg-[#C5A065]/10 bg-white/5 transition-all group"
                    >
                        <X size={18} className="group-hover:rotate-90 transition-transform duration-500" />
                    </button>
                </div>
            </header>

            {/* Main Content */}
            <AnimatePresence mode="wait">
                <motion.div
                    key={currentSlide}
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    transition={{ duration: 1.5 }}
                    className="relative flex-1 flex flex-col"
                >
                    {/* Background Layers */}
                    <div className="absolute inset-0 overflow-hidden">
                        <motion.div
                            style={{ x: bgX, y: bgY, scale: 1.1 }}
                            className="absolute inset-x-[-5%] inset-y-[-5%] w-[110%] h-[110%]"
                        >
                            <OptimizedImage
                                src={slide.image}
                                alt={slide.title}
                                className="w-full h-full object-cover brightness-[0.35]"
                            />
                        </motion.div>
                        <div className="absolute inset-0 bg-gradient-to-b from-black/80 via-transparent to-black" />
                        <EnvironmentalOverlay type={story.overlayType} />
                        <Particles />
                    </div>

                    {/* Narrative Zone */}
                    <div className="relative z-20 flex-1 container mx-auto px-6 md:px-12 flex flex-col justify-center max-w-6xl">
                        <motion.div
                            initial={{ opacity: 0, y: 30 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.5, duration: 1.2, ease: [0.16, 1, 0.3, 1] }}
                            className="space-y-8 md:space-y-12"
                        >
                            {/* Label */}
                            <div className="flex items-center gap-6">
                                <span className="h-px w-12 bg-[#C5A065]/50" />
                                <span className="text-[#C5A065] text-[10px] md:text-[11px] font-bold uppercase tracking-[0.8em]">
                                    {slide.type === "history" ? "Fragmen Sejarah" :
                                        slide.type === "mystery" ? "Misteri & Legenda" :
                                            slide.type === "ritual" ? "Ritual & Budaya" : "Eksplorasi"}
                                </span>
                            </div>

                            {/* Title */}
                            <h2 className="text-4xl md:text-8xl lg:text-9xl font-serif text-white leading-[1.1] md:leading-tight tracking-tight">
                                {slide.title}
                            </h2>

                            {/* Body */}
                            <AnimatedText
                                text={slide.content}
                                className="text-base md:text-2xl lg:text-3xl text-white/60 font-light leading-relaxed max-w-4xl font-serif italic"
                            />

                            {/* Trivia Trigger */}
                            {slide.trivia && (
                                <motion.div
                                    initial={{ opacity: 0 }}
                                    animate={{ opacity: 1 }}
                                    transition={{ delay: 2 }}
                                    className="pt-4"
                                >
                                    <button
                                        onClick={(e) => { e.stopPropagation(); setShowTrivia(!showTrivia); }}
                                        className="flex items-center gap-3 text-[9px] text-[#C5A065] hover:text-white uppercase tracking-[0.4em] font-bold transition-colors group"
                                    >
                                        <div className="w-8 h-8 rounded-full border border-[#C5A065]/20 flex items-center justify-center group-hover:border-[#C5A065] transition-all">
                                            <Info size={14} />
                                        </div>
                                        Fakta Tersembunyi
                                    </button>
                                </motion.div>
                            )}

                            {/* Trivia Content */}
                            <AnimatePresence>
                                {showTrivia && (
                                    <motion.div
                                        initial={{ opacity: 0, y: 20 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        exit={{ opacity: 0, y: 20 }}
                                        className="fixed bottom-0 md:absolute md:bottom-24 left-0 md:left-auto right-0 md:right-12 max-w-none md:max-w-sm bg-black/95 md:bg-white/5 backdrop-blur-3xl border-t md:border border-white/10 p-8 md:rounded-[32px] z-50 shadow-2xl safe-bottom"
                                    >
                                        <p className="text-[10px] text-[#C5A065] font-black uppercase tracking-[0.4em] mb-4">Wawasan Tambahan</p>
                                        <p className="text-sm md:text-base text-white/70 font-serif leading-relaxed italic">"{slide.trivia}"</p>
                                        <button
                                            onClick={() => setShowTrivia(false)}
                                            className="absolute top-4 right-4 text-white/20 hover:text-white p-2"
                                        >
                                            <ChevronRight size={16} className="rotate-90" />
                                        </button>
                                    </motion.div>
                                )}
                            </AnimatePresence>

                            {/* CTA */}
                            <div className="pt-8">
                                {slide.type === "map" ? (
                                    <a
                                        href={story.mapUrl}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="flex items-center gap-8 group"
                                        onClick={(e) => e.stopPropagation()}
                                    >
                                        <motion.div
                                            whileHover={{ scale: 1.1 }}
                                            className="w-14 h-14 md:w-20 md:h-20 rounded-full border border-[#C5A065]/50 flex items-center justify-center text-[#C5A065] group-hover:bg-[#C5A065] group-hover:text-black transition-all duration-700 shadow-[0_0_50px_rgba(197,160,101,0.2)]"
                                        >
                                            <Compass size={24} className="md:w-8 md:h-8" />
                                        </motion.div>
                                        <div className="flex flex-col">
                                            <span className="text-[10px] md:text-xs font-bold uppercase tracking-[0.3em] md:tracking-[0.5em] text-white">Buka di Google Maps</span>
                                            <span className="text-[8px] md:text-[10px] text-white/20 tracking-[0.2em] md:tracking-[0.3em] font-medium mt-1 uppercase">Navigasi Langsung Via Satelit</span>
                                        </div>
                                    </a>
                                ) : (
                                    <button
                                        onClick={(e) => { e.stopPropagation(); nextSlide(); }}
                                        className="flex items-center gap-6 group"
                                    >
                                        <div className="w-12 h-12 md:w-16 md:h-16 rounded-full border border-white/10 flex items-center justify-center text-white/30 group-hover:border-[#C5A065] group-hover:text-[#C5A065] transition-all duration-700">
                                            <ChevronRight size={20} className="md:w-6 md:h-6 group-hover:translate-x-1 transition-transform" />
                                        </div>
                                        <div className="flex flex-col text-left">
                                            <span className="text-[10px] md:text-xs font-bold uppercase tracking-[0.4em] md:tracking-[0.6em] text-white/70 group-hover:text-[#C5A065] transition-colors">Lanjutkan</span>
                                            <span className="text-[8px] md:text-[10px] text-white/20 tracking-[0.2em] md:tracking-[0.3em] font-medium mt-1 uppercase">Fragmen Berikutnya</span>
                                        </div>
                                    </button>
                                )}
                            </div>
                        </motion.div>
                    </div>
                </motion.div>
            </AnimatePresence>

            {/* Manual Navigation Areas */}
            <div className="absolute inset-y-0 left-0 w-[15%] z-30 cursor-w-resize" onClick={(e) => { e.stopPropagation(); prevSlide(); }} />
            <div className="absolute inset-y-0 right-0 w-[15%] z-30 cursor-e-resize" onClick={(e) => { e.stopPropagation(); nextSlide(); }} />

            {/* Footer */}
            <footer className="fixed bottom-0 left-0 right-0 h-auto px-6 md:px-12 z-[20000] flex items-center justify-between pointer-events-none py-8 safe-bottom">
                <div className="flex items-center gap-12">
                    <div className="flex flex-col">
                        <span className="text-[8px] font-black text-white/5 uppercase tracking-[0.6em] mb-1 uppercase">Destinasi</span>
                        <span className="text-[10px] md:text-xs font-bold tracking-[0.3em] text-[#C5A065] uppercase">{story.title}</span>
                    </div>
                </div>

                <div className="pointer-events-auto flex items-center gap-10">
                    <div className="hidden lg:block text-right">
                        <span className="text-[8px] font-black text-white/10 uppercase tracking-[0.6em] mb-2 block">Interaksi</span>
                        <span className="text-[9px] text-white/20 uppercase tracking-[0.4em] font-bold">Tahan Klik Untuk Jeda</span>
                    </div>
                    <div className="flex gap-4">
                        <button
                            onClick={(e) => { e.stopPropagation(); prevSlide(); }}
                            className="p-3 md:p-5 border border-white/5 rounded-full hover:bg-white/5 transition-all text-white/20 hover:text-white"
                        >
                            <ChevronLeft size={16} />
                        </button>
                        <button
                            onClick={(e) => { e.stopPropagation(); nextSlide(); }}
                            className="p-3 md:p-5 border border-white/5 rounded-full hover:bg-white/5 transition-all text-white/20 hover:text-white"
                        >
                            <ChevronRight size={16} />
                        </button>
                    </div>
                </div>
            </footer>
        </div>
    );
};

export default DiscoveryStory;

"use client";

import React, { useState, useEffect } from "react";
import Navbar from "@/components/Navbar";
import Footer from "@/components/Footer";
import { motion, useScroll, AnimatePresence } from "framer-motion";
import { MapPin, Info, Sparkles, Filter, ChevronRight, Bell, ArrowDown } from "lucide-react";
import { eventData, EventItem, EventType } from "@/data/events";
import { Link } from "react-router-dom";
import { useLanguage } from "@/context/LanguageContext";
import EventCard3D from "@/components/EventCard3D";

// Helper for Countdown
const CountdownUnit = ({ value, label }: { value: number; label: string }) => (
    <div className="flex flex-col items-center mx-3 md:mx-8 min-w-[60px] md:min-w-[100px]">
        <span className="text-[clamp(2.5rem,10vw,4.5rem)] font-serif text-[#C5A065] font-light leading-none mb-2">
            {value.toString().padStart(2, "0")}
        </span>
        <span className="text-[9px] md:text-xs font-bold uppercase tracking-[0.2em] md:tracking-[0.3em] text-white/40">
            {label}
        </span>
    </div>
);

const KalenderAcara = () => {
    const { lang, t } = useLanguage();
    const [filter, setFilter] = useState<EventType | "All">("All");
    const [timeLeft, setTimeLeft] = useState({ days: 0, hours: 0, minutes: 0, seconds: 0 });

    const { scrollYProgress } = useScroll();

    useEffect(() => {
        window.scrollTo(0, 0);

        // Simple countdown logic to a fixed future date (e.g., Festival Tanjung Lesung)
        const targetDate = new Date();
        targetDate.setDate(targetDate.getDate() + 14);
        targetDate.setHours(targetDate.getHours() + 5);

        const timer = setInterval(() => {
            const now = new Date();
            const difference = targetDate.getTime() - now.getTime();

            if (difference > 0) {
                setTimeLeft({
                    days: Math.floor(difference / (1000 * 60 * 60 * 24)),
                    hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
                    minutes: Math.floor((difference / 1000 / 60) % 60),
                    seconds: Math.floor((difference / 1000) % 60),
                });
            }
        }, 1000);

        return () => clearInterval(timer);
    }, []);

    const localizedEvents = eventData.map(item => {
        if (lang === "ID") return item;

        // Basic EN overrides for main events
        const overrides: any = {
            1: { title: "New Year's Day", date: "Jan 1", month: "January", description: "Global year-end celebration. Usually livened up with fireworks at Carita Beach and Tanjung Lesung." },
            10: { title: "Isra Mi'raj", date: "Jan 16", month: "January", description: "Commemoration of Prophet Muhammad's holy journey. A moment of spiritual reflection for Muslims in Pandeglang." },
            11: { title: "Chinese New Year", date: "Feb 17", month: "February", description: "New Year celebration for the Chinese community. Decorated with red ornaments and dragon dances." },
            13: { title: "Nyepi Day", date: "Mar 19", month: "March", description: "Day of silence for Hindus. A time for meditation and self-purification." },
            15: { title: "Eid al-Fitr 1447 H", date: "Mar 21-22", month: "March", description: "Victory day for Muslims after a month of fasting. A time for family gatherings and forgiveness." },
            14: { title: "Pandeglang Regency Anniversary", date: "Apr 1", month: "April", description: "Celebration of the 152nd anniversary of Pandeglang. Livened up with cultural parades and SME exhibitions." },
            2: { title: "Seba Baduy", location: "Kanekes & Rangkasbitung", date: "May 1-3", month: "May", description: "Annual tradition of the Baduy Tribe walking to meet regional leaders with agricultural products as gratitude." },
            4: { title: "Tanjung Lesung Festival", location: "KEK Tanjung Lesung", date: "Sep 4-6", month: "September", description: "International tourism festival with coastal culture, sailing parade, and mangrove conservation campaign." },
            5: { title: "Rampak Bedug Festival", location: "Pandeglang Square", date: "Aug 15-20", month: "August", description: "Stunning traditional percussion art of Banten. A provincial competition celebrating Indonesia's Independence Day." }
        };

        const override = overrides[item.id];
        if (override) {
            return { ...item, ...override };
        }
        return item;
    });

    const filteredItems = localizedEvents.filter(item =>
        filter === "All" ? true : item.type === filter
    );

    return (
        <div className="min-h-screen bg-[#050505] text-white selection:bg-[#C5A065] selection:text-black overflow-x-hidden">
            <Navbar />

            {/* CINEMATIC HERO SPOTLIGHT */}
            <header className="relative min-h-[90svh] md:h-screen flex flex-col items-center justify-center overflow-hidden py-24 md:py-0">
                {/* Background Layer */}
                <div className="absolute inset-0 z-0">
                    <img
                        src="https://images.unsplash.com/photo-1492684223066-81342ee5ff30?w=1600&auto=format&fit=crop&q=60"
                        alt="Event Atmosphere"
                        className="w-full h-full object-cover opacity-20 md:opacity-30 transform scale-105 animate-[kenburns_20s_infinite_alternate]"
                    />
                    <div className="absolute inset-0 bg-gradient-to-b from-[#050505]/95 via-transparent to-[#050505]"></div>
                </div>

                {/* Content Layer */}
                <div className="relative z-10 container mx-auto px-6 text-center">
                    <motion.div
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 1 }}
                    >
                        <div className="inline-flex items-center gap-2 mb-8 px-4 py-1.5 border border-[#C5A065]/30 rounded-full bg-[#C5A065]/5 backdrop-blur-md">
                            <Sparkles className="text-[#C5A065] animate-pulse" size={12} />
                            <span className="text-[#C5A065] text-[9px] md:text-[10px] font-black tracking-[0.25em] md:tracking-[0.3em] uppercase">
                                {lang === "ID" ? "Sorotan Utama" : "Featured Spotlight"}
                            </span>
                        </div>

                        <h1 className="text-[clamp(2.5rem,12vw,6rem)] font-serif font-medium leading-[0.9] tracking-tighter mb-8 text-transparent bg-clip-text bg-gradient-to-br from-white via-white to-gray-500 px-4">
                            Festival <span className="italic text-[#C5A065]">Tanjung Lesung</span>
                        </h1>

                        <p className="text-white/40 text-base md:text-lg font-light tracking-wide max-w-2xl mx-auto mb-16 px-6">
                            {lang === "ID"
                                ? "Saksikan perpaduan harmonis antara olahraga ekstrem, budaya pesisir, dan keindahan samudra dalam satu panggung megah."
                                : "Witness the harmonious blend of extreme sports, coastal culture, and oceanic beauty in one magnificent stage."}
                        </p>

                        {/* COUNTDOWN */}
                        <div className="flex justify-center items-center divide-x divide-white/10 mb-20 px-4">
                            <CountdownUnit value={timeLeft.days} label={lang === "ID" ? "Hari" : "Days"} />
                            <CountdownUnit value={timeLeft.hours} label={lang === "ID" ? "Jam" : "Hours"} />
                            <CountdownUnit value={timeLeft.minutes} label={lang === "ID" ? "Min" : "Mins"} />
                            <CountdownUnit value={timeLeft.seconds} label={lang === "ID" ? "Detik" : "Secs"} />
                        </div>

                        <motion.div
                            animate={{ y: [0, 10, 0] }}
                            transition={{ repeat: Infinity, duration: 2 }}
                            className="opacity-30"
                        >
                            <span className="text-[8px] uppercase tracking-[0.4em] mb-4 block">
                                {lang === "ID" ? "Jelajahi Agenda Lain" : "Explore Other Agenda"}
                            </span>
                            <ArrowDown className="mx-auto text-[#C5A065]" size={18} />
                        </motion.div>
                    </motion.div>
                </div>
            </header>

            {/* FILTER & TIMELINE SECTION */}
            <section className="py-20 bg-[#0a0a0a] relative">
                {/* Filter Tabs */}
                <div className="container mx-auto px-4 mb-24">
                    <div className="flex flex-wrap justify-center gap-8 border-b border-white/10 pb-8">
                        <button
                            onClick={() => setFilter("All")}
                            className={`text-xs font-bold uppercase tracking-[0.2em] transition-all hover:text-[#C5A065] ${filter === "All" ? "text-white" : "text-white/30"}`}
                        >
                            {lang === "ID" ? "Semua" : "All"}
                        </button>
                        <button
                            onClick={() => setFilter("Event")}
                            className={`text-xs font-bold uppercase tracking-[0.2em] transition-all hover:text-[#C5A065] ${filter === "Event" ? "text-white" : "text-white/30"}`}
                        >
                            {lang === "ID" ? "Event Budaya" : "Cultural Events"}
                        </button>
                        <button
                            onClick={() => setFilter("Holiday")}
                            className={`text-xs font-bold uppercase tracking-[0.2em] transition-all hover:text-[#C5A065] ${filter === "Holiday" ? "text-white" : "text-white/30"}`}
                        >
                            {lang === "ID" ? "Hari Libur" : "Holidays"}
                        </button>
                    </div>
                </div>

                {/* TIMELINE CONTAINER */}
                <div className="container mx-auto px-4 relative max-w-7xl">
                    <div className="space-y-12">
                        {filteredItems.map((item, index) => (
                            <EventCard3D key={item.id} item={item} index={index} lang={lang as "ID" | "EN"} />
                        ))}
                    </div>

                    {/* End Line Decoration */}
                    <div className="text-center mt-32 relative z-10">
                        <div className="w-2 h-32 bg-gradient-to-b from-[#C5A065] to-transparent mx-auto opacity-20"></div>
                        <span className="text-[#C5A065] text-[10px] font-bold tracking-[0.5em] uppercase mt-4 block">
                            {lang === "ID" ? "Akhir Agenda 2026" : "End of 2026 Agenda"}
                        </span>
                    </div>
                </div>
            </section>

            <Footer />
        </div>
    );
};

export default KalenderAcara;

export interface EarthquakeData {
    datetime: string;
    magnitude: string;
    depth: string;
    location: string;
    coordinates: string;
    potensi: string;
    shakemap?: string;
}

export interface WeatherData {
    time: string;
    status: string;
    temp: string;
    humidity: string;
    windDirection: string;
    windSpeed: string;
}

const BMKG_API_BASE = "/bmkg-api/DataMKG";

export const fetchLatestEarthquake = async (): Promise<EarthquakeData | null> => {
    try {
        const response = await fetch(`${BMKG_API_BASE}/TEWS/autogempa.json`);
        if (!response.ok) throw new Error("Failed to fetch earthquake data");
        const data = await response.json();
        const gempa = data.Infogempa.gempa;

        return {
            datetime: `${gempa.Tanggal} ${gempa.Jam}`,
            magnitude: gempa.Magnitude,
            depth: gempa.Kedalaman,
            location: gempa.Wilayah,
            coordinates: gempa.Coordinates,
            potensi: gempa.Potensi,
            shakemap: gempa.Shakemap
        };
    } catch (error) {
        console.error("BMKG Earthquake Service Error:", error);
        return null;
    }
};

export const fetchWeatherPandeglang = async (): Promise<WeatherData | null> => {
    try {
        const response = await fetch(`${BMKG_API_BASE}/MEWS/DigitalForecast/DigitalForecast-Banten.xml`);
        if (!response.ok) throw new Error("Failed to fetch weather data");
        const xmlText = await response.text();

        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(xmlText, "text/xml");

        const areas = xmlDoc.getElementsByTagName("area");
        let pandeglangNode: Element | null = null;

        for (let i = 0; i < areas.length; i++) {
            if (areas[i].getAttribute("description") === "Pandeglang") {
                pandeglangNode = areas[i];
                break;
            }
        }

        if (!pandeglangNode) return null;

        const params = pandeglangNode.getElementsByTagName("parameter");
        const weatherNodes = Array.from(params).find(p => p.getAttribute("id") === "weather")?.getElementsByTagName("timerange");
        const tempNodes = Array.from(params).find(p => p.getAttribute("id") === "t")?.getElementsByTagName("timerange");
        const humNodes = Array.from(params).find(p => p.getAttribute("id") === "hu")?.getElementsByTagName("timerange");
        const wsNodes = Array.from(params).find(p => p.getAttribute("id") === "ws")?.getElementsByTagName("timerange");

        if (!weatherNodes || !tempNodes) return null;

        // Extract Current (0), 6h, 12h, and 24h to check for upcoming disasters
        const latestWeather = weatherNodes[0].getElementsByTagName("value")[0].textContent || "1";
        const latestTemp = tempNodes[0].getElementsByTagName("value")[0].textContent || "27";
        const latestHum = humNodes ? humNodes[0].getElementsByTagName("value")[0].textContent : "80";
        const latestWS = wsNodes ? wsNodes[0].getElementsByTagName("value")[0].textContent : "10";

        return {
            time: new Date().toISOString(),
            status: getWeatherDesc(latestWeather),
            temp: latestTemp,
            humidity: `${latestHum}%`,
            windDirection: "Variable",
            windSpeed: `${latestWS} km/h`
        };
    } catch (error) {
        console.error("BMKG Weather Service Error:", error);
        return null;
    }
};

export const getRealWeather = async (): Promise<any[]> => {
    try {
        const response = await fetch(`${BMKG_API_BASE}/MEWS/DigitalForecast/DigitalForecast-Banten.xml`);
        if (!response.ok) throw new Error("Failed to fetch weather data");
        const xmlText = await response.text();

        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(xmlText, "text/xml");

        const areas = xmlDoc.getElementsByTagName("area");
        const relevantAreas = ["Pandeglang", "Labuan", "Carita", "Panimbang", "Sumur", "Mandalawangi"];
        const weatherList = [];

        for (let i = 0; i < areas.length; i++) {
            const areaName = areas[i].getAttribute("description");
            if (areaName && relevantAreas.includes(areaName)) {
                const params = areas[i].getElementsByTagName("parameter");
                const weatherNodes = Array.from(params).find(p => p.getAttribute("id") === "weather")?.getElementsByTagName("timerange");
                const tempNodes = Array.from(params).find(p => p.getAttribute("id") === "t")?.getElementsByTagName("timerange");
                const humNodes = Array.from(params).find(p => p.getAttribute("id") === "hu")?.getElementsByTagName("timerange");
                const wsNodes = Array.from(params).find(p => p.getAttribute("id") === "ws")?.getElementsByTagName("timerange");

                if (weatherNodes && tempNodes) {
                    const latestWeather = weatherNodes[0].getElementsByTagName("value")[0].textContent || "1";
                    const latestTemp = tempNodes[0].getElementsByTagName("value")[0].textContent || "27";
                    const latestHum = humNodes ? humNodes[0].getElementsByTagName("value")[0].textContent : "80";
                    const latestWS = wsNodes ? wsNodes[0].getElementsByTagName("value")[0].textContent : "10";

                    weatherList.push({
                        location: areaName,
                        condition: getWeatherConditionName(latestWeather),
                        temp: parseInt(latestTemp),
                        humidity: parseInt(latestHum),
                        windSpeed: parseInt(latestWS),
                        description: getWeatherDesc(latestWeather)
                    });
                }
            }
        }

        return weatherList.length > 0 ? weatherList : [];
    } catch (error) {
        console.error("BMKG Multi-Weather Error:", error);
        return [];
    }
};

const getWeatherConditionName = (code: string): string => {
    const codes: Record<string, string> = {
        "0": "Sunny",
        "1": "Partly Cloudy",
        "2": "Partly Cloudy",
        "3": "Cloudy",
        "4": "Cloudy",
        "60": "Rainy",
        "61": "Rainy",
        "63": "Rainy",
        "95": "Stormy",
        "97": "Stormy"
    };
    return codes[code] || "Partly Cloudy";
};

const getWeatherDesc = (code: string): string => {
    const codes: Record<string, string> = {
        "0": "Cerah",
        "1": "Cerah Berawan",
        "2": "Cerah Berawan",
        "3": "Berawan",
        "4": "Berawan Tebal",
        "5": "Udara Kabur",
        "10": "Asap",
        "45": "Kabut",
        "60": "Hujan Ringan",
        "61": "Hujan Sedang",
        "63": "Hujan Lebat (Waspada Banjir)",
        "80": "Hujan Lokal",
        "95": "Hujan Petir (Bahaya Petir)",
        "97": "Hujan Petir (Bahaya Petir)"
    };
    return codes[code] || "Berawan";
};

