import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Sun, Cloud, CloudRain, AlertTriangle, ShieldCheck, MapPin, ChevronRight, X } from "lucide-react";
import { useLanguage } from "@/context/LanguageContext";

const LiveStatus = () => {
    const { lang, t } = useLanguage();
    const [index, setIndex] = useState(0);
    const [isExpanded, setIsExpanded] = useState(false);

    const statuses = lang === "ID" ? [
        {
            id: "krakatau",
            location: "Anak Krakatau",
            status: "Level II (Waspada)",
            icon: AlertTriangle,
            color: "text-orange-500",
            bg: "bg-orange-500/10",
            detail: "Aman untuk dikunjungi dengan jarak aman 5km dari kawah aktif."
        },
        {
            id: "lesung",
            location: "Tanjung Lesung",
            status: "Cerah Berawan",
            icon: Sun,
            color: "text-amber-500",
            bg: "bg-amber-500/10",
            detail: "Suhu 29Â°C. Kecepatan angin tenang, sangat ideal untuk water sport."
        },
        {
            id: "ujungkulon",
            location: "Ujung Kulon",
            status: "Akses Terbuka",
            icon: ShieldCheck,
            color: "text-emerald-500",
            bg: "bg-emerald-500/10",
            detail: "Izin masuk (Simaksi) tersedia di Labuan dan Sumur. Jalur trekking kering."
        }
    ] : [
        {
            id: "krakatau",
            location: "Anak Krakatau",
            status: "Level II (Alert)",
            icon: AlertTriangle,
            color: "text-orange-500",
            bg: "bg-orange-500/10",
            detail: "Safe to visit with a 5km safety distance from the active crater."
        },
        {
            id: "lesung",
            location: "Tanjung Lesung",
            status: "Partly Cloudy",
            icon: Sun,
            color: "text-amber-500",
            bg: "bg-amber-500/10",
            detail: "Temp 29Â°C. Calm wind speeds, ideal for water sports."
        },
        {
            id: "ujungkulon",
            location: "Ujung Kulon",
            status: "Access Open",
            icon: ShieldCheck,
            color: "text-emerald-500",
            bg: "bg-emerald-500/10",
            detail: "Permits (Simaksi) available in Labuan and Sumur. Trekking trails are dry."
        }
    ];

    useEffect(() => {
        const timer = setInterval(() => {
            if (!isExpanded) {
                setIndex((prev) => (prev + 1) % statuses.length);
            }
        }, 5000);
        return () => clearInterval(timer);
    }, [isExpanded]);

    const current = statuses[index];

    const [isMenuOpen, setIsMenuOpen] = useState(false);

    useEffect(() => {
        const checkMenu = () => {
            setIsMenuOpen(document.body.style.overflow === 'hidden');
        };
        const observer = new MutationObserver(checkMenu);
        observer.observe(document.body, { attributes: true, attributeFilter: ['style'] });
        return () => observer.disconnect();
    }, []);

    if (isMenuOpen) return null;

    return (
        <div className="fixed top-20 right-4 md:top-24 md:right-8 z-[100] flex flex-col items-end pointer-events-auto">
            <AnimatePresence mode="wait">
                <motion.div
                    key={isExpanded ? "expanded" : current.id}
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    exit={{ opacity: 0, x: -20 }}
                    className={`relative group bg-white/90 backdrop-blur-2xl border border-white/40 shadow-2xl transition-all duration-700 ${isExpanded ? "rounded-[1.5rem] md:rounded-[2rem] w-[calc(100vw-2rem)] max-w-xs p-6 md:p-8" : "rounded-full py-2.5 px-5 md:py-3 md:px-6 cursor-pointer hover:shadow-2xl"
                        }`}
                    onClick={() => !isExpanded && setIsExpanded(true)}
                >
                    {isExpanded ? (
                        <div className="relative">
                            <button
                                onClick={(e) => { e.stopPropagation(); setIsExpanded(false); }}
                                className="absolute -top-4 -right-4 w-8 h-8 rounded-full bg-black text-white flex items-center justify-center hover:scale-110 transition-transform"
                            >
                                <X size={14} />
                            </button>

                            <div className="flex items-center gap-3 mb-6">
                                <div className={`p-3 rounded-2xl ${current.bg} ${current.color}`}>
                                    <current.icon size={20} />
                                </div>
                                <div>
                                    <h5 className="text-[10px] font-bold uppercase tracking-[0.2em] text-gray-400">{lang === "ID" ? "Status Terkini" : "Live Status"}</h5>
                                    <p className="font-serif text-lg text-black">{current.location}</p>
                                </div>
                            </div>

                            <div className="space-y-4">
                                <div className="flex items-center justify-between text-sm">
                                    <span className="text-gray-400">{lang === "ID" ? "Kondisi" : "Condition"}</span>
                                    <span className={`font-bold ${current.color}`}>{current.status}</span>
                                </div>
                                <p className="text-xs text-gray-500 leading-relaxed font-light">
                                    {current.detail}
                                </p>
                                <button className="w-full mt-4 flex items-center justify-between p-4 rounded-2xl bg-black text-white text-[10px] font-bold uppercase tracking-widest group/btn">
                                    {lang === "ID" ? "Lihat Detail Peta" : "View Map Detail"}
                                    <ChevronRight size={14} className="group-hover/btn:translate-x-1 transition-transform" />
                                </button>
                            </div>

                            {/* Status Switcher Dots */}
                            <div className="flex justify-center gap-2 mt-8">
                                {statuses.map((_, i) => (
                                    <button
                                        key={i}
                                        onClick={(e) => { e.stopPropagation(); setIndex(i); }}
                                        className={`h-1 rounded-full transition-all duration-500 ${index === i ? "w-8 bg-[#C5A065]" : "w-2 bg-gray-200"}`}
                                    />
                                ))}
                            </div>
                        </div>
                    ) : (
                        <div className="flex items-center gap-3">
                            <div className={`w-2 h-2 rounded-full animate-pulse ${current.id === 'krakatau' ? 'bg-orange-500' : 'bg-emerald-500'}`}></div>
                            <div className="flex items-center gap-2">
                                <current.icon size={14} className={current.color} />
                                <span className="text-[10px] font-bold uppercase tracking-[0.2em] text-black/40">{current.location}:</span>
                                <span className="text-[10px] font-bold uppercase tracking-[0.2em] text-black">{current.status}</span>
                            </div>
                        </div>
                    )}
                </motion.div>
            </AnimatePresence>
        </div>
    );
};

