                        <div className="inline-flex items-center gap-2 mb-8 px-4 py-1.5 border border-[#C5A065]/30 rounded-full bg-[#C5A065]/5 backdrop-blur-md">
                            <Sparkles className="text-[#C5A065] animate-pulse" size={12} />
                            <span className="text-[#C5A065] text-[9px] md:text-[10px] font-black tracking-[0.25em] md:tracking-[0.3em] uppercase">
                                {lang === "ID" ? "Sorotan Utama" : "Featured Spotlight"}
                            </span>
                        </div>

                        <h1 className="text-[clamp(2.5rem,12vw,6rem)] font-serif font-medium leading-[0.9] tracking-tighter mb-8 text-transparent bg-clip-text bg-gradient-to-br from-white via-white to-gray-500 px-4">
                            Festival <span className="italic text-[#C5A065]">Tanjung Lesung</span>
                        </h1>

                        <p className="text-white/40 text-base md:text-lg font-light tracking-wide max-w-2xl mx-auto mb-16 px-6">
                            {lang === "ID"
                                ? "Saksikan perpaduan harmonis antara olahraga ekstrem, budaya pesisir, dan keindahan samudra dalam satu panggung megah."
                                : "Witness the harmonious blend of extreme sports, coastal culture, and oceanic beauty in one magnificent stage."}
                        </p>

                        {/* COUNTDOWN */}
                        <div className="flex justify-center items-center divide-x divide-white/10 mb-20 px-4">
                            <CountdownUnit value={timeLeft.days} label={lang === "ID" ? "Hari" : "Days"} />
                            <CountdownUnit value={timeLeft.hours} label={lang === "ID" ? "Jam" : "Hours"} />
                            <CountdownUnit value={timeLeft.minutes} label={lang === "ID" ? "Min" : "Mins"} />
                            <CountdownUnit value={timeLeft.seconds} label={lang === "ID" ? "Detik" : "Secs"} />
                        </div>

                        <motion.div
                            animate={{ y: [0, 10, 0] }}
                            transition={{ repeat: Infinity, duration: 2 }}
                            className="opacity-30"
                        >
                            <span className="text-[8px] uppercase tracking-[0.4em] mb-4 block">
                                {lang === "ID" ? "Jelajahi Agenda Lain" : "Explore Other Agenda"}
                            </span>
                            <ArrowDown className="mx-auto text-[#C5A065]" size={18} />
                        </motion.div>
                    </motion.div>
                </div>
            </header>

            {/* FILTER & TIMELINE SECTION */}
            <section className="py-20 bg-[#0a0a0a] relative">
                {/* Filter Tabs */}
                <div className="container mx-auto px-4 mb-24">
                    <div className="flex flex-wrap justify-center gap-8 border-b border-white/10 pb-8">
                        <button
                            onClick={() => setFilter("All")}
                            className={`text-xs font-bold uppercase tracking-[0.2em] transition-all hover:text-[#C5A065] ${filter === "All" ? "text-white" : "text-white/30"}`}
                        >
                            {lang === "ID" ? "Semua" : "All"}
                        </button>
                        <button
                            onClick={() => setFilter("Event")}
                            className={`text-xs font-bold uppercase tracking-[0.2em] transition-all hover:text-[#C5A065] ${filter === "Event" ? "text-white" : "text-white/30"}`}
                        >
                            {lang === "ID" ? "Event Budaya" : "Cultural Events"}
                        </button>
                        <button
                            onClick={() => setFilter("Holiday")}
                            className={`text-xs font-bold uppercase tracking-[0.2em] transition-all hover:text-[#C5A065] ${filter === "Holiday" ? "text-white" : "text-white/30"}`}
                        >
                            {lang === "ID" ? "Hari Libur" : "Holidays"}
                        </button>
                    </div>
                </div>

                {/* TIMELINE CONTAINER */}
                <div className="container mx-auto px-4 relative max-w-5xl">
                    {/* The Golden Line */}
                    <div className="absolute left-4 md:left-1/2 top-0 bottom-0 w-px bg-gradient-to-b from-[#C5A065]/0 via-[#C5A065]/50 to-[#C5A065]/0 md:-translate-x-1/2 h-full"></div>

                    <div className="space-y-24">
                        {filteredItems.map((item, index) => (
                            <motion.div
                                key={item.id}
                                initial={{ opacity: 0, y: 50 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                viewport={{ once: true, margin: "-100px" }}
                                transition={{ duration: 0.8, delay: index * 0.1 }}
                                className={`relative flex flex-col md:flex-row gap-8 md:gap-20 items-center ${index % 2 === 0 ? "md:flex-row" : "md:flex-row-reverse"}`}
                            >
                                {/* Timeline Dot */}
                                <div className="absolute left-4 md:left-1/2 w-3 h-3 bg-[#0a0a0a] border border-[#C5A065] rounded-full z-10 md:-translate-x-1/2 shadow-[0_0_15px_#C5A065]">
                                    <div className="absolute inset-0 bg-[#C5A065]/30 animate-ping rounded-full"></div>
                                </div>

                                {/* Content Side */}
                                <div className="pl-12 md:pl-0 w-full md:w-1/2 text-left md:text-right">
                                    <div className={`flex flex-col ${index % 2 === 0 ? "md:items-end" : "md:items-start"}`}>
                                        <span className="text-4xl md:text-6xl font-serif text-[#C5A065] opacity-20 font-bold mb-2">
                                            {item.date}
                                        </span>
                                        <h3 className="text-2xl md:text-4xl font-serif text-white mb-4 leading-tight">
                                            {item.title}
                                        </h3>
                                        <div className={`flex items-center gap-4 text-xs font-bold uppercase tracking-widest text-white/40 mb-6 ${index % 2 === 0 ? "md:flex-row-reverse" : "md:flex-row"}`}>
                                            <span className={`${item.type === "Event" ? "text-[#C5A065]" : "text-blue-400"}`}>
                                                {item.type === "Event" ? (lang === "ID" ? item.category : (item.category === "Kuliner" ? "Culinary" : (item.category === "Pariwisata" ? "Tourism" : "Art"))) : (lang === "ID" ? "Nasional" : "National")}
                                            </span>
                                            <span className="w-1 h-1 bg-white/20 rounded-full"></span>
                                            {item.location && <span>{item.location}</span>}
                                        </div>
                                        <p className={`text-white/60 font-light leading-relaxed mb-6 max-w-sm ${index % 2 === 0 ? "md:text-right" : "md:text-left"}`}>
                                            {item.description}
                                        </p>
                                        <Link
                                            to={`/kalender/${item.id}`}
                                            className={`inline-flex items-center gap-2 text-xs font-bold uppercase tracking-[0.2em] text-white hover:text-[#C5A065] transition-colors ${index % 2 === 0 ? "md:flex-row-reverse" : "md:flex-row"}`}
                                        >
                                            {lang === "ID" ? "Lihat Detail" : "View Details"} <ChevronRight size={14} />
                                        </Link>
                                    </div>
                                </div>

                                {/* Image Side */}
                                <div className="pl-12 md:pl-0 w-full md:w-1/2">
                                    <div className="relative aspect-[4/3] rounded-2xl overflow-hidden group border border-white/5">
                                        <div className="absolute inset-0 bg-black/20 group-hover:bg-transparent transition-all duration-500 z-10"></div>
                                        <img
                                            src={item.image || "https://images.unsplash.com/photo-1533174072545-7a4b6ad7a6c3?w=800&auto=format&fit=crop&q=60"}
                                            alt={item.title}
                                            className="w-full h-full object-cover filter grayscale group-hover:grayscale-0 transition-all duration-1000 transform group-hover:scale-110"
                                        />
                                        <div className="absolute bottom-4 right-4 z-20 opacity-0 group-hover:opacity-100 transition-all duration-500 translate-y-4 group-hover:translate-y-0 text-white">
                                            <Info size={20} />
                                        </div>
                                    </div>
                                </div>
                            </motion.div>
                        ))}
                    </div>

                    {/* End Line Decoration */}
                    <div className="text-center mt-32 relative z-10">
                        <div className="w-2 h-32 bg-gradient-to-b from-[#C5A065] to-transparent mx-auto opacity-20"></div>
                        <span className="text-[#C5A065] text-[10px] font-bold tracking-[0.5em] uppercase mt-4 block">
                            {lang === "ID" ? "Akhir Agenda 2026" : "End of 2026 Agenda"}
                        </span>
                    </div>
                </div>
            </section>

            <Footer />
        </div>
    );
};

export default KalenderAcara;

